/*
 * Script: parseMultiLineString.ts
 * Description: Reads a multi-line string of ability entries, extracts "name" and "effect",
 *              and outputs a properly-quoted CSV with headers including extra columns for future use.
 */

// Define CSV headers, including future columns
const headers = ['name', 'effect', 'details'];

function main() {
// Split lines, trim, and keep only those starting with '|'
  const lines = effects
    .split('\n')
    .map(line => line.trim())
    .filter(line => line.startsWith('|'));

// Parse each valid line to extract name and effect
  const rows = lines.map(line => {
    const match = line.match(/^\|\s*([^|]+?)\s*\|\s*([^|]+?)\s*\|\s*([^|]+?)\s*\|/);
    if (!match) {
      return { name: '', effect: '', details: '' };
    }
    return {
      name: match[1].trim(),
      effect: match[2].trim(),
      details: match[3].trim()
    };
  });

// Build CSV lines with proper quoting
  const csvLines = [];
  csvLines.push(headers.map(quoteCSV).join(','));
  rows.forEach(row => {
    const values = headers.map(header => {
      const val = (row as any)[header] || '';
      return quoteCSV(val);
    });
    csvLines.push(values.join(','));
  });

// Output CSV
  const csvOutput = csvLines.join('\n');
  console.log(csvOutput);
}




// Helper to quote values for CSV
function quoteCSV(value: string): string {
  const escaped = value.replace(/"/g, '""');
  return `"${escaped}"`;
}

